// Filter for organisers in the drip campaign
const members = $input.all()[0].json;
const today = new Date().toISOString().split('T')[0];

// Filter for members in the Organiser drip campaign who are due for next email
const eligibleOrganisers = members.filter(member => {
  const notes = member.Notes || '';

  // Check if they're in the Organiser drip campaign
  const inDripCampaign = notes.includes('DripCampaign: Organiser');

  // Extract drip stage
  let dripStage = -1;
  if (inDripCampaign) {
    const stageMatch = notes.match(/DripStage:\s*(\d+)/);
    if (stageMatch && stageMatch[1]) {
      dripStage = parseInt(stageMatch[1], 10);
    }
  }

  // Extract next drip date
  let nextDripDate = null;
  if (inDripCampaign) {
    const dateMatch = notes.match(/NextDripDate:\s*(\d{4}-\d{2}-\d{2})/);
    if (dateMatch && dateMatch[1]) {
      nextDripDate = dateMatch[1];
    }
  }

  // Check if today is on or after the next drip date
  const isDue = nextDripDate && today >= nextDripDate;

  return inDripCampaign && isDue && dripStage >= 0 && dripStage < 5; // We have 5 emails in the drip campaign
});

console.log(`Found ${eligibleOrganisers.length} organisers due for drip emails`);

// Process each eligible organiser
return eligibleOrganisers.map(member => {
  // Extract current drip stage
  const notes = member.Notes || '';
  let dripStage = 0;
  const stageMatch = notes.match(/DripStage:\s*(\d+)/);
  if (stageMatch && stageMatch[1]) {
    dripStage = parseInt(stageMatch[1], 10);
  }

  return {
    json: {
      ...member,
      firstName: member.FirstName || 'Community Member',
      dripStage: dripStage,
      nextDripStage: dripStage + 1,
      nextDripDate: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000).toISOString().split('T')[0] // 7 days from now
    }
  };
});
